    protected static $allStrings = null;

    /**
     * Returns a single string by key from all merged strings
     *
     * @param string $key
     *
     * @return string
     */
    public static function get($key)
    {
        if (self::$allStrings === null) {
            self::$allStrings = self::getAllStrings();
        }

        return self::$allStrings[$key] ?? '';
    }

